import React from "react";
import Popup from "reactjs-popup";
import { useRef, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../api/axios";

export default function RestaurantCard({ restaurant }) {
  const userId = localStorage.getItem("userId");
  const BOOKING_URL = "https://skole.mortengh.dk/PHP/booking.php";
  const navigate = useNavigate();
  const userRef = useRef();
  const errRef = useRef();

  const [howMany, setHowMany] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  //   const [restaurantID, setRestaurantID] = useState("");
  //   const [usersID, setUsersID] = useState("");
  const [errMsg, setErrMsg] = useState("");

  useEffect(() => {
    setErrMsg("");
  }, [howMany, date, time]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // setRestaurantID(restaurant.id);
    // setUsersID(userId);
    axios
      .post(
        BOOKING_URL,
        JSON.stringify({
          howMany,
          date,
          time,
          restaurantID: restaurant.id,
          usersID: userId,
        })
      )
      .then((result) => {
        // console.log(result.data);
        if (result.data.status == "invalid") {
          setErrMsg(result.data.errorMessage);
        } else {
          setHowMany("");
          setDate("");
          setTime("");
          //   setRestaurantID("");
          //   setUsersID("");
          navigate("/Bookings");
        }
      });
  };

  return (
    <article className="RestaurantCard">
      <img src={restaurant.img} alt={restaurant.title} />
      <div className="TextContainer">
        <h2>{restaurant.resName}</h2>
        <p>
          Adresse: {restaurant.resAddress}, {restaurant.postnr}{" "}
          {restaurant.city}
        </p>
        <p>Kapacitet: {restaurant.capacity}</p>
        <Popup
          trigger={<button> Se Restaurant</button>}
          position="right center"
        >
          <div>
            <h2>{restaurant.resName}</h2>
            <p>Adresse: {restaurant.resAddress}</p>
            <p>Kapacitet: {restaurant.capacity}</p>
            <div className="BookingForm">
              <p
                ref={errRef}
                className={errMsg ? "ErrorMessage" : "offScreen"}
                aria-live="assertive"
              >
                {errMsg}
              </p>
              <form onSubmit={handleSubmit}>
                <div className="field">
                  <label htmlFor="howMany">Hvor mange?</label>
                  <input
                    name="howMany"
                    type="number"
                    onChange={(e) => setHowMany(e.currentTarget.value)}
                    value={howMany}
                    required
                  />
                </div>
                <div className="field">
                  <label htmlFor="date">Dato:</label>
                  <input
                    name="date"
                    type="date"
                    onChange={(e) => setDate(e.currentTarget.value)}
                    value={date}
                    required
                  />
                </div>
                <div className="field">
                  <label htmlFor="time">Tid:</label>
                  <input
                    name="time"
                    type="time"
                    step="1800"
                    min="08:00"
                    max="21:00"
                    onChange={(e) => setTime(e.currentTarget.value)}
                    value={time}
                    required
                  />
                </div>
                {/* <input
                name="restaurantID"
                type="hidden"
                value={restaurant.id}
                onChange={(e) => setRestaurantID(restaurant.id)}
              />
              <input
                name="userID"
                type="hidden"
                value={userId}
                onChange={(e) => setUsersID(userId)}
              /> */}
                <button className="btn">Book</button>
              </form>
            </div>
          </div>
        </Popup>
      </div>
    </article>
  );
}
