import { useState, useEffect } from "react";
import {useNavigate} from 'react-router-dom';
import $ from "jquery";
import "../App.css";
import parse from 'html-react-parser';

const Registration = () => {

function RegistrationFunc() {
  const navigate = useNavigate();
  const initialValues = {firstname: "", lastname: "", mail: "", age: "", phonenr: "", pwd: "", resite: ""};
  const [signupValues, setSignupValues] = useState(initialValues);
  const [signupErrors, setSignupErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
    const handleChange = (e) => {
      const {name, value} = e.target;
      setSignupValues({ ...signupValues, [name]: value});
       console.log(signupValues);
    };



    const handleSumbit = (e) => {
        e.preventDefault();
        setSignupErrors(validateSignup(signupValues));
        setIsSubmit(true);
    };

    useEffect(()=> {
        console.log(signupErrors);
        if(Object.keys(signupErrors).lenght === 0 && isSubmit){
            console.log(signupValues);
        }
    },[signupErrors]);

    const validateSignup = (values) => {
        const errors = {};
        const regex = /^\S+@\S+\.\S+$/;
        if(!values.firstname){
            errors.firstname = "Fornavn skal udfyldes";
        }
        if(!values.lastname){
            errors.firstname = "Efternavn skal udfyldes";
        }
        if(!values.mail){
            errors.firstname = "E-mail skal udfyldes";
        }
        if(!values.age){
            errors.firstname = "Alder skal udfyldes";
        }
        if(!values.phonenr){
            errors.firstname = "Tlf. nr. skal udfyldes";
        }
        if(!values.pwd){
            errors.firstname = "Kodeord skal udfyldes";
        }
        if(values.pwd && values.pwd != values.resite){
            errors.firstname = "Kodeordene er ikke ens";
        }

        return errors;
    }

    // const handleRedirect = (e) =>{
    //     if(error === ''){
    //         return navigate('/login');
    //     }
    // }


    return (

        <div className="App">
            <h1>Registration</h1>
            <div className="RegistrationContainer">
                <pre>{JSON.stringify(signupValues, undefined, 2)}</pre>
            <form
                className="RegistrationForm"
                action="http://localhost:8000/signup.php"
                method="post"
                onSubmit={(event) => handleSumbit(event)}
            >
                {/* {error ? <div className="FormErrors">{parse(error)}</div> : <div></div>} */}


                <input placeholder="Fornavn" type="text" id="firstname" name="firstname" value={signupValues.firstname} onChange={(event) => handleChange(event)} />

                <input placeholder="Efternavn" type="text" id="lastname" name="lastname" value={signupValues.lastname} onChange={(event) => handleChange(event)} />

                <input placeholder="E-mail" type="text" id="mail" name="mail" value={signupValues.mail} onChange={(event) => handleChange(event)} />

                <input placeholder="Alder" type="text" id="age" name="age" value={signupValues.age} onChange={(event) => handleChange(event)} />

                <input placeholder="Tlf. nr." type="tel" id="phonenr" name="phonenr" value={signupValues.phonenr} onChange={(event) => handleChange(event)} />

                <input placeholder="Kodeord" type="password" id="pwd" name="pwd" value={signupValues.pwd} onChange={(event) => handleChange(event)} />

                <input placeholder="Gentag Kodeord" type="password" id="resite" name="resite" value={signupValues.resite} onChange={(event) => handleChange(event)} />

                <button type="submit">Registrer</button>
            </form>
            </div>
            <h1></h1>
        </div>
    );
}

return RegistrationFunc();

};

export default Registration;
