import React from "react";
import { useEffect, useState } from "react";
import RestaurantCard from "../components/RestaurantCard";

const Home = () => {
  const getUserData = localStorage.getItem("userId");
  const [restaurants, setRestaurants] = useState([]);
  const [searchCity, setSearchCity] = useState("");

  async function getRestaurants() {
    const url = `https://skole.mortengh.dk/PHP/restaurants.php?city=${searchCity}`;
    const response = await fetch(url);
    const data = await response.json();
    const restaurantssArray = Object.keys(data).map((key) => ({
      key: key,
      ...data[key],
    })); // from object to array
    setRestaurants(restaurantssArray);
  }
  useEffect(() => {
    getRestaurants();
  }, []);

  const handleChange = (e) => {
    setSearchCity(e.target.value);
    getRestaurants();
  };

  const handleClear = () => {
    getRestaurants();
    setSearchCity("");
  };

  return (
    <>
      <div className="Filters">
        <label htmlFor="searchbar">SÃ¸g efter By: </label>
        <input
          name="searchbar"
          type="search"
          value={searchCity}
          onChange={(event) => handleChange(event)}
        />
        <button class="btn" onClick={handleClear}>
          Clear
        </button>
      </div>
      <div className="RestaurantGrid">
        {restaurants.map((restaurant) => {
          return (
            <RestaurantCard restaurant={restaurant} key={restaurant.key} />
          );
        })}
      </div>
    </>
  );
};

export default Home;
