import { useState } from "react";
import $ from "jquery";
import "../App.css";
const Registration = () => {

function RegistrationFunc() {
    const [firstname, setFirstname] = useState("");
    const [lastname, setLastname] = useState("");
    const [mail, setMail] = useState("");
    const [age, setAge] = useState("");
    const [phonenr, setPhonenr] = useState("");
    const [result, setResult] = useState("");
    const [error, setError] = useState("");

    const handleChangeFirstname = (e) => {
        setFirstname(e.target.value);

    };
    const handleChangeLastname = (e) => {
      setLastname(e.target.value);
     };
    const handleChangeMail = (e) => {
      setMail(e.target.value);
    };
    const handleChangeAge = (e) => {
      setAge(e.target.value);
    };
    const handleChangePhonenr = (e) => {
        setPhonenr(e.target.value);
      };

    const handleSumbit = (e) => {
        e.preventDefault();
        const form = $(e.target);
        $.ajax({
            type: "POST",
            url: form.attr("action"),
            data: form.serialize(),
            success(data) {
                setResult(data);
            },
        });
    };

    return (

        <div className="App">
            <h1>Registration</h1>
            <div className="RegistrationContainer">

            <form
                className="RegistrationForm"
                action="http://localhost:8000/signup.php"
                method="post"
                onSubmit={(event) => handleSumbit(event)}
            >
                {result ? 
                <div className="ErrorMessage">{result}</div> : <div></div>
                }


                <input placeholder="First name" type="text" id="firstname" name="firstname" value={firstname} onChange={(event) => handleChangeFirstname(event)} />

                <input placeholder="Last name" type="text" id="lastname" name="lastname" value={lastname} onChange={(event) => handleChangeLastname(event)} />

                <input placeholder="E-mail" type="text" id="mail" name="mail" value={mail} onChange={(event) => handleChangeMail(event)} />

                <input placeholder="Age" type="text" id="age" name="age" value={age} onChange={(event) => handleChangeAge(event)} />

                <input placeholder="phonenr" type="tel" id="phonenr" name="phonenr" value={phonenr} onChange={(event) => handleChangePhonenr(event)} />
                <button type="submit">Registrer</button>
            </form>
            </div>
            <h1></h1>
        </div>
    );
}
return RegistrationFunc();
};

export default Registration;
