import {useRef, useState, useEffect, useContext} from 'react';
import AuthContext from "../context/AuthProvider";
import axios from "../api/axios";
const LOGIN_URL = 'http://localhost:8000/login.php';

const Login = () => {
    const { setAuth } = useContext(AuthContext);
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [succes, setSucces] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const handleSubmit = async (e) => {
        e.preventDefault();

        axios.post(LOGIN_URL, JSON.stringify({mail: user, pwd}))
        .then((result) => {
            console.log(result.data.status);
            if(result.data.Status == 'invalid'){
                alert('invalid user');
            }else if(result.data.status == 'valid') {
            setAuth({user, pwd});
            setUser('');
            setPwd('');
            setSucces(true);
            }
        }
        )

        // try{
        //     const response = await axios.post(LOGIN_URL, JSON.stringify({mail: user, pwd}),
        //     {
        //         headers: {'Content-Type': 'application/json'},
        //         // withCredentials: true
        //     }
        //     );
        //     console.log(JSON.stringify(response?.data));
        //     setAuth({user, pwd});
        //     setUser('');
        //     setPwd('');
        //     setSucces(true);
        // } catch (err){
        //     if(!err?.response){
        //         setErrMsg('No server response');
        //     } else if (err.response?.status === 400){
        //         setErrMsg('Missing username or password');
        //     }else if (err.response?.status === 401){
        //         setErrMsg('Unauthorized');
        //     }else {
        //         setErrMsg('login failed');
        //     }
        //     errRef.current.focus();
        // }
    }

        return (
            <>
            {succes ? (
            <section>
                <h1>You are logged in!</h1>
            </section>
            ) : (
            <div className="App">
                <div className="loginForm">
                    <p ref={errRef} className={errMsg ? "errMsg" : "offScreen"} aria-live="assertive">{errMsg}</p>
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor='username'>Email</label>
                        <input type="text" id="username" ref={userRef} onChange={(e) => setUser(e.currentTarget.value)} value={user} required></input>
                        <label htmlFor='password'>Kodeord</label>
                        <input type="password" id="password" ref={userRef} onChange={(e) => setPwd(e.currentTarget.value)} value={pwd} required></input>
                        <button>Log ind</button>
                    </form>
                </div>
            </div>
                )}
            </>
        )
  }
  export default Login;
