import { useRef, useState, useEffect } from "react";
import imgPlaceholder from "../images/placeholder.png";

import axios from "../api/axios";
import { useNavigate, useLocation } from "react-router-dom";
const SIGNUP_URL = "https://skole.mortengh.dk/PHP/resSignup.php";

const ResSignup = () => {
  const userRef = useRef();
  const errRef = useRef();

  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [mail, setMail] = useState("");
  const [capacity, setCapacity] = useState("");
  const [phonenr, setPhonenr] = useState("");
  const [image, setImage] = useState("");
  const [errMsg, setErrMsg] = useState("");

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setErrMsg("");
  }, [name, address, mail, capacity, phonenr]);

  function handleImageChange(event) {
    const file = event.target.files[0];
    if (file.size < 500000) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
      setErrMsg("");
    } else {
      setErrMsg("Billedet er for stort");
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault();

    axios
      .post(
        SIGNUP_URL,
        JSON.stringify({
          resName: name,
          resAddress: address,
          mail,
          capacity,
          phonenr,
        })
      )
      .then((result) => {
        console.log(result.data);
        if (result.data.status == "invalid") {
          setErrMsg(result.data.errorMessage);
        } else {
          setName("");
          setAddress("");
          setMail("");
          setCapacity("");
          setPhonenr("");
        }
      });
  };

  return (
    <div className="App">
      <div className="createRestaurantContainer">
        <div className="restaurantForm">
          <h1>Opret Restaurant</h1>
          <p
            ref={errRef}
            className={errMsg ? "ErrorMessage" : "offScreen"}
            aria-live="assertive"
          >
            {errMsg}
          </p>

          <form onSubmit={handleSubmit}>
            <label htmlFor="name">Restaurant navn</label>
            <input
              type="text"
              id="name"
              ref={userRef}
              onChange={(e) => setName(e.currentTarget.value)}
              value={name}
              required
            ></input>
            <label htmlFor="address">Adresse</label>
            <input
              type="text"
              id="address"
              ref={userRef}
              onChange={(e) => setAddress(e.currentTarget.value)}
              value={address}
              required
            ></input>
            <label htmlFor="mail">Mail</label>
            <input
              type="email"
              id="mail"
              ref={userRef}
              onChange={(e) => setMail(e.currentTarget.value)}
              value={mail}
              required
            ></input>
            <label htmlFor="capacity">Kapacitet</label>
            <input
              type="number"
              id="capacity"
              ref={userRef}
              onChange={(e) => setCapacity(e.currentTarget.value)}
              value={capacity}
              required
            ></input>
            <label htmlFor="phonenr">Tlf. nr.</label>
            <input
              type="tel"
              id="phonenr"
              ref={userRef}
              onChange={(e) => setPhonenr(e.currentTarget.value)}
              value={phonenr}
              required
            ></input>
            <label htmlFor="image">Billede</label>
            <input
              type="file"
              className="file-input"
              accept="image/*"
              onChange={handleImageChange}
            />
            <img
              className="image-preview"
              src={image}
              alt="choose"
              onError={(event) => (event.target.src = imgPlaceholder)}
            />
            <button>Opret</button>
          </form>
        </div>
      </div>
    </div>
  );
};
export default ResSignup;
