import {useRef, useState, useEffect, useContext} from 'react';


const Login = () => {
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [succes, setSucces] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(user, pwd);
        setUser('');
        setPwd('');
        setSucces(true);
    }

        return (
            <>
            {succes ? (
            <section>
                <h1>You are logged in!</h1>
            </section>
            ) : (
            <div className="App">
                <div className="loginForm">
                    <p ref={errRef} className={errMsg ? "errMsg" : "offScreen"} aria-live="assertive">{errMsg}</p>
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor='username'>Email</label>
                        <input type="text" id="username" ref={userRef} onChange={(e) => setUser(e.currentTarget.value)} value={user} required></input>
                        <label htmlFor='password'>Kodeord</label>
                        <input type="password" id="password" ref={userRef} onChange={(e) => setPwd(e.currentTarget.value)} value={pwd} required></input>
                        <button>Log ind</button>
                    </form>
                </div>
            </div>
                )}
            </>
        )
  }
  export default Login;
