import React from "react";
import { useEffect, useState } from "react";
import RestaurantCard from "../components/RestaurantCard";

const Home = () => {
  const getUserData = localStorage.getItem("userId");
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    async function getRestaurants() {
      const url = "https://skole.mortengh.dk/PHP/restaurants.php";
      const response = await fetch(url);
      const data = await response.json();
      const restaurantssArray = Object.keys(data).map((key) => ({
        key: key,
        ...data[key],
      })); // from object to array
      setRestaurants(restaurantssArray);
    }
    getRestaurants();
  }, []);

  return (
    <div className="RestaurantGrid">
      {restaurants.map((restaurant) => {
        return <RestaurantCard restaurant={restaurant} key={restaurant.key} />;
      })}
    </div>
  );
};

export default Home;
