import {useRef, useState, useEffect} from 'react';
import useAuth from '../hooks/useAuth';
import axios from "../api/axios";
import { Link, useNavigate, useLocation } from 'react-router-dom';
const LOGIN_URL = 'http://localhost:8000/login.php';

const Login = () => {
    const { setAuth } = useAuth();

    const navigate = useNavigate();
    const location = useLocation();
    const from = location.state?.from?.pathname || "/";


    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');


    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])

    const handleSubmit = async (e) => {
        e.preventDefault();

        axios.post(LOGIN_URL, JSON.stringify({mail: user, pwd}))
        .then((result) => {
            console.log(result.data);
            if(result.data.status == 'invalid'){
                setErrMsg(result.data.errorMessage);
            }else  {
            setAuth({user, pwd});
            localStorage.setItem(setAuth({user, pwd}));
            setUser('');
            setPwd('');
            navigate(from, { replace: true });
            }
        }
        )
    }

        return (
            <div className="App">
                <div className="loginForm">
                    <p ref={errRef} className={errMsg ? "errMsg" : "offScreen"} aria-live="assertive">{errMsg}</p>
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor='username'>Email</label>
                        <input type="text" id="username" ref={userRef} onChange={(e) => setUser(e.currentTarget.value)} value={user} required></input>
                        <label htmlFor='password'>Kodeord</label>
                        <input type="password" id="password" ref={userRef} onChange={(e) => setPwd(e.currentTarget.value)} value={pwd} required></input>
                        <button>Log ind</button>
                    </form>
                </div>
            </div>
        )
  }
  export default Login;
