import React from "react";
import Popup from "reactjs-popup";
import { useRef, useState, useEffect } from "react";

import axios from "../api/axios";
const BOOKING_URL = "https://skole.mortengh.dk/PHP/booking.php";

const userRef = useRef();
const errRef = useRef();

const [name, setName] = useState("");
const [address, setAddress] = useState("");
const [mail, setMail] = useState("");
const [capacity, setCapacity] = useState("");
const [phonenr, setPhonenr] = useState("");
const [errMsg, setErrMsg] = useState("");

useEffect(() => {
  userRef.current.focus();
}, []);

useEffect(() => {
  setErrMsg("");
}, [name, address, mail, capacity, phonenr]);

const handleSubmit = async (e) => {
  e.preventDefault();

  axios
    .post(
      SIGNUP_URL,
      JSON.stringify({
        resName: name,
        resAddress: address,
        mail,
        capacity,
        phonenr,
      })
    )
    .then((result) => {
      console.log(result.data);
      if (result.data.status == "invalid") {
        setErrMsg(result.data.errorMessage);
      } else {
        setName("");
        setAddress("");
        setMail("");
        setCapacity("");
        setPhonenr("");
      }
    });
};

export default function RestaurantCard({ restaurant }) {
  const userId = localStorage.getItem("userId");
  return (
    <article>
      {/* <img src={post.image} alt={post.title} /> */}
      <h2>{restaurant.resName}</h2>
      <p>Adresse: {restaurant.resAddress}</p>
      <p>Kapacitet: {restaurant.capacity}</p>
      <Popup trigger={<button> Se Restaurant</button>} position="right center">
        <div>
          <h2>{restaurant.resName}</h2>
          <p>Adresse: {restaurant.resAddress}</p>
          <p>Kapacitet: {restaurant.capacity}</p>
          <div className="BookingForm">
            <form>
              <div className="field">
                <label htmlFor="howMany">Hvor mange?</label>
                <input name="howMany" type="number" />
              </div>
              <div className="field">
                <label htmlFor="date">Dato:</label>
                <input name="date" type="date" />
              </div>
              <div className="field">
                <label htmlFor="time">Tid:</label>
                <input name="time" type="time" />
              </div>
              <input name="restaurantID" type="hidden" value={restaurant.id} />
              <input name="userID" type="hidden" value={userId} />
              <button>Book</button>
            </form>
          </div>
        </div>
      </Popup>
    </article>
  );
}
