  const userId = localStorage.getItem("userId");

  const dateTime = booking.date+' '+booking.time;
  const bookingTime = new Date(dateTime);
  let formattedDate = format(bookingTime, "dd/M/yyyy");
  let formattedTime = format(bookingTime, "H:mm");


  async function deleteBooking() {
    const confirmDelete = window.confirm(`Vil du annullere bookingen hos ${booking.resName} d. ${formattedDate} kl. ${formattedTime}?`);
