import {useRef, useState, useEffect} from 'react';

import axios from "../api/axios";
import { useNavigate, useLocation } from 'react-router-dom';
const SIGNUP_URL = 'https://skole.mortengh.dk/PHP/resSignup.php';

const ResSignup = () => {





    const userRef = useRef();
    const errRef = useRef();

    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [mail, setMail] = useState('');
    const [capacity, setCapacity] = useState('');
    const [phonenr, setPhonenr] = useState('');
    const [menucard, setMenucard] = useState('');
    const [errMsg, setErrMsg] = useState('');

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [name, address, mail, capacity, phonenr, menucard])

    const handleSubmit = async (e) => {
        e.preventDefault();

        axios.post(SIGNUP_URL, JSON.stringify({mail: user, pwd}))
        .then((result) => {
            console.log(result.data);
            if(result.data.status == 'invalid'){
                setErrMsg(result.data.errorMessage);
            }else  {
            setName('');
            setAddress('');
            setMail('');
            setCapacity('');
            setPhonenr('');
            setMenucard('');

            }
        }
        )
    }

        return (
            <div className="App">
                <div className='LoginContainer'>
                <div className="LoginForm">
                <h1>Log ind</h1>
                    <p ref={errRef} className={errMsg ? "ErrorMessage" : "offScreen"} aria-live="assertive">{errMsg}</p>

                    <form onSubmit={handleSubmit}>
                        <label htmlFor='username'>Email</label>
                        <input type="text" id="username" ref={userRef} onChange={(e) => setUser(e.currentTarget.value)} value={user} required></input>
                        <label htmlFor='password'>Kodeord</label>
                        <input type="password" id="password" ref={userRef} onChange={(e) => setPwd(e.currentTarget.value)} value={pwd} required></input>
                        <button>Log ind</button>
                    </form>
                </div>
                </div>
            </div>
        )
  }
  export default ResSignup;
